import rclpy
from rclpy.node import Node
import serial  # PySerial library

class ArduinoListener(Node):
    def __init__(self):
        super().__init__('arduino_listener')
        self.get_logger().info("Initializing Arduino Listener Node...")
        
        # Update this with the correct port for your Arduino (e.g., COM3, /dev/ttyUSB0)
        self.serial_port = serial.Serial('COM5', 9600, timeout=1)
        
        # Timer to read from Arduino every second
        self.create_timer(1.0, self.read_from_arduino)

    def read_from_arduino(self):
        if self.serial_port.in_waiting > 0:  # Check if data is available
            line = self.serial_port.readline().decode('utf-8').strip()
            self.get_logger().info(f"Received from Arduino: {line}")


def main(args=None):
    rclpy.init(args=args)
    node = ArduinoListener()
    try:
        rclpy.spin(node)
    except KeyboardInterrupt:
        pass
    finally:
        node.destroy_node()
        rclpy.shutdown()


if __name__ == '__main__':
    main()
